import styled from "styled-components";

const Leftside = (props) => {
    return (
        <Container>
            <ArtCard>
                <UserInfo>
                    <CardBackground />
                    <a>
                        <Photo />
                        <Link>Welcome, there!</Link>
                    </a>
                    <a>
                        <AddPhotoText>Add a photo</AddPhotoText>
                    </a>
                </UserInfo>
                <Widget>
                    <a>
                        <div>
                            <span>Connections</span>
                           <span>Grow your network</span>
                        </div>
                           
                    </a>
                </Widget>
            </ArtCard>
        </Container>  
    );
};

const Container = styled.div`
    grid-area: leftside;
`;

const ArtCard = styled.div`
    text-align: center;
    overflow: hidden;
    margin-bottom: 8px;
    background-color: #fff;
    border-radius: 5px;
    transition: box-shadow 83ms;
    position: relative;
    border: none;
    box-shadow: 0 0 0 1px rgb(0 0 0 / 15%), 0 0 0 rgb(0 0 0 / 20%);
`;

const UserInfo = styled.div`
    border-bottom: 1px solid rgb(0 0 0 0.15);
    padding: 12px 12px 16px;
    word-wrap: break-word;                  /* Property is used to indicate whether the web browser can make line breaks within words. This is necessary to prevent an overflow if an otherwise inseparable text would be too long for the box it contains.*/
    word-break: break-word;
`;

const CardBackground = styled.div`
    background: url("images/card-bg.svg");
    background-position: center;
    background-size: 462px;
    height: 54px;
    margin: -12px -12px 0;
`;

const Photo = styled.div`
    box-shadow: none;
    background-image: url("/images/photo.svg");
    width: 77px;
    height: 77px;
    box-sizing: border-box;
    background-clip: content-box;
    background-position: center;
    background-size: 60%;
    background-repeat: no-repeat;            /*prevents the repetition of the image*/    
    border: 2px solid white; 
    margin: -38px auto 12px;                /*puts the image in the right position*/
    border-radius: 50%;
`;

const Link = styled.div`
    font-size: 16px;
    line-height: 1.5;           /*space between the lines*/
    color: rgba(0, 0, 0, 0.9);
    font-weight: 600;
`

const AddPhotoText = styled.div`
    color: #0a66c2;
    margin-top: 4px;
    font-size: 12px;
    line-height: 1.33;
    font-weight: 400;
`;

const Widget = styled.div`

`;

export default Leftside;
