import styled from "styled-components";

const Leftside = (props) => {
    return (
        <Container>
            <ArtCard>
                <UserInfo>
                    <CardBackground />
                    <a>
                        <Photo />
                        <Link>Welcome, there!</Link>
                    </a>
                    <a>
                        <AddPhotoText>Add a photo</AddPhotoText>
                    </a>
                </UserInfo>
            </ArtCard>
        </Container>  
    );
};

const Container = styled.div`
    grid-area: leftside;
`;

const ArtCard = styled.div`
    text-align: center;
    overflow: hidden;
    margin-bottom: 8px;
    background-color: #fff;
    border-radius: 5px;
    transition: box-shadow 83ms;
    position: relative;
    border: none;
    box-shadow: 0 0 0 1px rgb(0 0 0 / 15%), 0 0 0 rgb(0 0 0 / 20%);
`;

const UserInfo = styled.div`
    border-bottom: 1px solid rgb(0 0 0 0.15);
    padding: 12px 12px 16px;
    word-wrap: break-word;                  /* Property is used to indicate whether the web browser can make line breaks within words. This is necessary to prevent an overflow if an otherwise inseparable text would be too long for the box it contains.*/
    word-break: break-word;
`;

const CardBackground = styled.div`
    background: url("images/card-bg.svg");
    background-position: center;
    background-size: 462px;
    height: 54px;
    margin: -12px -12px 0;
`;

const Photo = styled.div`
    box-shadow: none;
    background-image: url("/images/photo.svg");
    width: 77px;
    height: 77px;
    box-sizing: border-box;
    background-clip: content-box;
    background-position: center;
    background-size: 60%
`;

const Link = styled.div`

`

const AddPhotoText = styled.div`

`;

export default Leftside;
